(function () { tinymce.PluginManager.requireLangPack('filion'); tinymce.create('tinymce.plugins.FilionPlugin', { init: function (ed, url) { ed.addCommand('mceExample', function () { ed.windowManager.open({ file: url + '/up.aspx', width: 630 + parseInt(ed.getLang('filion.delta_width', 0)), height: 313 + parseInt(ed.getLang('filion.delta_height', 0)), inline: 1 }, { plugin_url: url, some_custom_arg: 'custom arg' }) }); ed.addButton('filion', { title: 'Loading photos', cmd: 'mceExample', image: url + '/img/folder_picture.png' }); ed.onNodeChange.add(function (ed, cm, n) { cm.setActive('filion', n.nodeName == 'IMG') }) }, createControl: function (n, cm) { return null }, getInfo: function () { return { longname: 'Example plugin', author: 'Some author', authorurl: 'http://tinymce.moxiecode.com', infourl: 'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example', version: "1.0"} } }); tinymce.PluginManager.add('filion', tinymce.plugins.FilionPlugin) })();